#!/usr/bin/env bash
find /var/log -type f -mtime +30 -print0 | xargs -0 tar -czf /tmp/old-logs-archive.tar.gz --remove-files || true
echo "Archived logs older than 30 days to /tmp/old-logs-archive.tar.gz"

